import { dopplerNames } from './doppler_variable_names';

import * as fs from 'fs';

const main = async () => {
  const names = Object.keys(dopplerNames).filter((name) => name.includes('NEXT_PUBLIC_'));

  console.log(
    '! generating config types for the following secrets: ',
    names.join(', ')
  );

  const data = new Uint8Array(Buffer.from(
    '// THIS FILE IS AUTOMATICALLY GENERATED. DO NOT DIRECTLY MODIFY THIS FILE.\n' +
    '// YOUR CHANGES ARE LIKELY TO BE OVERRIDDEN IN THE FUTURE\n' +
    'export enum Doppler {\n' +
    names
      .map((name) => '  ' + name + ' = \'' + name + '\',\n')
      .join('') +
    '}\n' +
    '\nexport function getEnv(name: Doppler): any {\n'+
    '  switch(name) {\n' +
    names
      .map((name) => '  case Doppler.' + name + ': return process.env.' + name + ';\n')
      .join('') +
    '  }\n' +
    '}\n' +
    '\nexport function getEnvBool(name: Doppler): boolean {\n' +
    '  const value = process.env[name];\n' +
    '  if (typeof value === \'boolean\') {\n' +
    '    return value;\n' +
    '  } else if (value === \'true\') {\n' +
    '    return true;\n' +
    '  } else if (value === \'false\') {\n' +
    '    return false;\n' +
    '  } else {\n' +
    '    throw new Error(\'Not a boolean environment variable\');\n' +
    '  }\n' +
    '}'
  ));
  fs.writeFile('utils/env.ts', data, (err) => {
    if (err) throw err;
    console.log('The file has been saved!');
  });
};

main()
  .catch(console.error);

export {};